    // numbers < 1 aren't prime, abort recursion then
    if(d<2) return 1;
    // not divisible by d? then continue testing
    if(n % d != 0) return testPrime(n, d-1);
    // else return 0, e. g. n isn't prime
    return 0;
}

// n is prime, if not divisible by n/2 ... 2
int isPrime(int n) {
    return testPrime(n, n >> 1);
}

// print all primes <= n
void printPrimes(int n) {
    // 2 is the smallest prime --> no recursion for n=2
    if(n==2) { printf("2 "); return; }
    // recursion: print all primes < n
    printPrimes(n-1);
    // n is prime? then print it
    if(isPrime(n)) printf("%d ", n);
}

int main() {
    printPrimes(500);
    printf("&#92;n");
    return 0;
}

